use std::convert::Infallible;

use base64ct::Encoding;
use bytes::Bytes;
use chrono::{DateTime, Utc};
use futures::stream;
use http::StatusCode;
use mpart_async::client::MultipartRequest;
use serde_json::{json, Value};
use spectral::assert_that;
use spectral::prelude::{OptionAssertions, OrderedAssertions};

use crate::common::url;

mod common;

const IMAGE_BASE64: &str = "";
const IMAGE_CONTENT_TYPE: &str = "image/jpeg";

#[actix_web::test]
async fn image_upload_serve_delete() {
    let now = Utc::now();

    let image = Bytes::from(base64ct::Base64::decode_vec(IMAGE_BASE64).unwrap());
    let image_len = image.len();
    let image_stream: Vec<std::result::Result<Bytes, Infallible>> = vec![Ok(image.clone())];

    let client = common::client_logged_in().await.unwrap();
    let client_anon = common::client().unwrap();

    let mut req = MultipartRequest::default();
    req.add_stream(
        "file",
        "test-file.jpg",
        IMAGE_CONTENT_TYPE,
        stream::iter(image_stream),
    );

    let mut upload_resp = client
        .post(url("/images?description=test+image"))
        .content_type(format!(
            "multipart/form-data; boundary={}",
            req.get_boundary()
        ))
        .send_body(actix_http::body::BodyStream::new(req))
        .await
        .unwrap();

    assert_that!(upload_resp.status()).is_equal_to(StatusCode::OK);

    let uploaded = upload_resp.json::<Value>().await.unwrap();
    let uploaded_id = uploaded.get("id").unwrap().as_str().unwrap().to_string();
    let uploaded_date = uploaded
        .get("uploaded")
        .unwrap()
        .as_str()
        .unwrap()
        .to_string();
    let expected_meta = json!({
        "id": uploaded_id,
        "length": image_len,
        "filename": "test-file.jpg",
        "uploaded": uploaded_date,
        "metadata": {
            "description": "test image",
            "contentType": "image/jpeg",
        }
    });
    assert_that!(uploaded).is_equal_to(&expected_meta);
    assert_that!(DateTime::parse_from_rfc3339(&uploaded_date)
        .unwrap()
        .with_timezone(&Utc))
    .is_greater_than_or_equal_to(now);

    let image_url = format!("/images/{}", uploaded_id);
    let mut serve_resp = client.get(url(&image_url)).send().await.unwrap();

    assert_that!(serve_resp.status()).is_equal_to(StatusCode::OK);
    assert_that!(serve_resp
        .headers()
        .get("Content-Type")
        .unwrap()
        .to_str()
        .unwrap())
    .is_equal_to(IMAGE_CONTENT_TYPE);

    let serve_body = serve_resp.body().await.unwrap();
    assert_that!(serve_body).is_equal_to(&image);

    let mut serve_anon_resp = client_anon.get(url(&image_url)).send().await.unwrap();

    assert_that!(serve_anon_resp.status()).is_equal_to(StatusCode::OK);
    assert_that!(serve_anon_resp
        .headers()
        .get("Content-Type")
        .unwrap()
        .to_str()
        .unwrap())
    .is_equal_to(IMAGE_CONTENT_TYPE);

    let serve_anon_body = serve_anon_resp.body().await.unwrap();
    assert_that!(serve_anon_body).is_equal_to(&image);

    let mut list_resp = client.get(url("/images")).send().await.unwrap();
    assert_that!(list_resp.status()).is_equal_to(StatusCode::OK);

    let list_body = list_resp.json::<Value>().await.unwrap();
    let found = list_body
        .get("data")
        .unwrap()
        .as_array()
        .unwrap()
        .iter()
        .find(|v| v.get("id").unwrap().as_str().unwrap() == uploaded_id)
        .unwrap();
    assert_that!(found).is_equal_to(&expected_meta);

    let delete_anon_resp = client_anon.delete(url(&image_url)).send().await.unwrap();
    assert_that!(delete_anon_resp.status()).is_equal_to(StatusCode::FORBIDDEN);

    let delete_resp = client.delete(url(&image_url)).send().await.unwrap();
    assert_that!(delete_resp.status()).is_equal_to(StatusCode::OK);

    let not_found_resp = client.get(url(&image_url)).send().await.unwrap();
    assert_that!(not_found_resp.status()).is_equal_to(StatusCode::NOT_FOUND);

    let not_found_anon_resp = client_anon.get(url(&image_url)).send().await.unwrap();
    assert_that!(not_found_anon_resp.status()).is_equal_to(StatusCode::NOT_FOUND);

    let mut list_not_found_resp = client.get(url("/images")).send().await.unwrap();
    assert_that!(list_not_found_resp.status()).is_equal_to(StatusCode::OK);

    let list_not_found_body = list_not_found_resp.json::<Value>().await.unwrap();
    let list_not_found = list_not_found_body
        .get("data")
        .unwrap()
        .as_array()
        .unwrap()
        .iter()
        .find(|v| v.get("id").unwrap().as_str().unwrap() == uploaded_id);
    assert_that!(list_not_found).is_none();
}

#[actix_web::test]
async fn cannot_upload_anon() {
    let image = Bytes::from(base64ct::Base64::decode_vec(IMAGE_BASE64).unwrap());
    let image_stream: Vec<std::result::Result<Bytes, Infallible>> = vec![Ok(image.clone())];

    let client = common::client().unwrap();

    let mut req = MultipartRequest::default();
    req.add_stream(
        "file",
        "test-file.jpg",
        IMAGE_CONTENT_TYPE,
        stream::iter(image_stream),
    );

    let upload_resp = client
        .post(url("/images?description=test+image"))
        .content_type(format!(
            "multipart/form-data; boundary={}",
            req.get_boundary()
        ))
        .send_body(actix_http::body::BodyStream::new(req))
        .await
        .unwrap();

    assert_that!(upload_resp.status()).is_equal_to(StatusCode::FORBIDDEN);
}
